module.exports = async (d) => {
  const inside = d.inside;
  const { abbreviate } = require('../../models/functions')

  if (!inside) {
    d.isError = true;
    return d.error(`❌ WhatscodeError: Usage: $abbreviate[number]!`);
  } else {
    try {
      var re = inside.trim() === "" ? false : abbreviate(inside);
    } catch (err) {
      d.isError = true;
      return d.error(
        `❌ WhatscodeError: Something error on $abbreviate!`
      );
    }

    return re;
  }
 
};
